    public long[] exportarIDsCadastro(){
        String cpf = Arrays.toString(jTxtCPF.getText().split("."));
        cpf = Arrays.toString(cpf.split("-"));
        long[] IDs ={jCBPacotes.getSelectedIndex()+1, Long.parseLong(cpf) };
        
        return IDs;
    }
    
