     public static Postos postos;
    
    private void exibir(){
       ArrayList<PostoDTO> lista = ConexaoDAO.getInstance().PostosList();
       DefaultTableModel tabmodel = (DefaultTableModel)jTable1.getModel(); 
       tabmodel.setNumRows(0);
       
       for (int i = 0; i < lista.size(); i++)
          tabmodel.addRow(new Object[]{lista.get(i).getId_posto(), lista.get(i).getNome_posto(),lista.get(i).getEndereco(), lista.get(i).getBaias()});
    }
    
    public static Postos getInstance(){
       if(postos == null) 
          postos = new Postos();
        return postos;
    }
    
    public  String exportarNomePosto(){
        return jTable1.getValueAt(jTable1.getSelectedRow(), 1).toString();
    }
    
    public int exportarIdPosto(){
        return Integer.parseInt(jTable1.getValueAt(jTable1.getSelectedRow(), 0).toString());
    }

